import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'

var idno;

 class View extends Component {

    constructor(props) 
    {
      super(props)
    
      this.state = {}
    }
                        
    
    

    render() {
            
         idno=this.props.match.params.userid;
             console.log(idno)

        return (<>
                    <div className="container text-center p-3">

                        <h3>User_View_Data</h3>

                        <div className="container text-center w-50">

                              <p>{idno} th Record Data</p>

                              <ul> 
                                  <li>{this.state.userid}</li>
                                  <li>{this.state.id}</li>
                                  <li>{this.state.name}</li>
                                  <li>{this.state.mail}</li>
                                  <li>{this.state.phone}</li>
                              </ul>

                              <Link to="/localapi1" className="btn btn-success">GoTo_Back</Link>

                        </div>
                        
                    </div>
        </>)
    }

    componentDidMount()
    {
           const url=`http://localhost:3001/user/${idno}` 

           axios.get(url).then((result) => this.setState(result.data));
    }
}


export default View;
