import React, { Component } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'

 
  var kumar;

 class View extends Component {

    constructor(props) 
    {
      super(props)
    
      this.state = 
                       {
                           viewdata:{} //empty object
                       }
    }
    
    

    render() {
            
        kumar = this.props.match.params.userid
             console.log(kumar)

        return (<>
                    <div className="container text-center p-3">

                        <h3>User_View_Data</h3>

                        <div className="container text-center w-50">

                              <p>{kumar} th Record Data</p>
                       
                              <tbody>
                        {
                            this.state.userData.map((res, i) => {
                                return (<tr key={i}>
                                    <td>{res.id}</td>
                                    <td>{res.name}</td>
                                    <td>{res.email}</td>
                                    <td>{res.phone}</td>
                                    <td>
                                        <Link to={`/restapi/view/${res.id}`} className="btn btn-primary">View</Link>
                                    </td>
                                               
                                    <td>
                                        <button className="btn btn-warning">Edit</button>
                                    </td>

                                    <td>
                                        <button className="btn btn-danger">Delete</button>
                                    </td>

                                </tr>)
                            })

                        }


                    </tbody>



                              <Link to="/localapi1" className="btn btn-success">GoTo_Back</Link>

                        </div>
                        
                    </div>
        </>)
    }

    componentDidMount()
    {
           const url=`http://localhost:3001/user${kumar}` 

           axios.get(url).then((result) => this.setState(result.data));
    }
}


export default View;
