import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'

var idno;

export default class View extends Component 
 
 {

    constructor(props) 
    {
      super(props)
    
      this.state = 
                   {
                       viewdata:{}
                   }
    }
                        
    
    

    render() 
    {
            
         idno=this.props.match.params.userid
         
             console.log(idno)

             

        return (<>
                    <div className="container text-center p-3">

                        <h3>User_View_Data</h3>

                        <div className="container text-center w-50">

                              <p>{idno} th Record Data</p>

                              <ul> 
                                  <li>{this.state.viewdata.userid}</li>
                                  <li>{this.state.viewdata.id}</li>
                                  <li>{this.state.viewdata.name}</li>
                                  <li>{this.state.viewdata.mail}</li>
                                  <li>{this.state.viewdata.phone}</li>
                              </ul>
                            

                              <Link to="/localapi1" className="btn btn-success">Go_To_Back</Link>

                        </div>
                        
                    </div>
        </>)
    }

    componentDidMount()
    {
        const url = `http://localhost:3001/users/${idno}`

        axios.get(url).then((result) => this.setState({viewdata:result.data}));

        //Update apidata to empty state object via setState
    }
}
