import React, { Component } from 'react'
import axios from 'axios'

const url = `http://localhost:3001/users`

export default class Signup extends Component {

    constructor(props) {
        super(props)

        this.state =
        {
            userid:'',
            id:'',
            uname: '',
            email: '',
            phone: '',
            txtConformation: ''
        }
    }
    useridHandling = (e) => {
        this.setState({ userid: e.target.value })
    }
    uidHandling = (e) => {
        this.setState({ uid: e.target.value })
    }

    //Name_input_textbox
    unameHandling = (e) => {
        this.setState({ uname: e.target.value })
    }


    //Email_input_textbox
    emailHandling = (e) => {
        this.setState({ email: e.target.value })
    }


    //Phone_input_textbox
    phoneHandling = (e) => {
        this.setState({ phone: e.target.value })
    }


    submitHandler = (e) => {

        e.preventDefault();

        var FormData =
        {   
            'userid': this.state.userid,
            'uid':this.state.uid,
            'name': this.state.uname,
            'email': this.state.email,
            'phone': this.state.phone
        }

        //alert(JSON.stringify(FormData));


        /*
        axios.post(url, formdata)
            .then(() => window.alert("....CREATED..."))
            .catch(() => window.alert("ERROR"));
        
        */

        /*
        axios.post(url, FormData)
            .then(() => this.setState({ txtConformation: 'Accounted Created...' }))
            .catch(() => this.setState({ txtConformation: 'Accounted Failed...' }));
        */

        axios.post(url, FormData)
            .then(() => {
                //window.alert("....CREATED..."); 
                this.props.history.push('/localapi1')
            });


    }


    render() {

        console.log(this.props)

        //Destructuring...
        var { uid,uname, email, phone, txtConformation } = this.state

        return (<>
            <div className="container-fluid     p-3">

                <h4 className="py-3 text-center">User Registration</h4>


                <div className="container forv  ">

                    <form onSubmit={this.submitHandler.bind(this)}>

                    <div class="mb-3">
                            <label className="form-label">userId</label>
                            <input type="text" class="form-control"
                                value={userid}
                                onChange={this.useridHandling.bind(this)} />

                        </div>

                        <div class="mb-3">
                            <label className="form-label">Id</label>
                            <input type="text" class="form-control"
                                value={uid}
                                onChange={this.uidHandling.bind(this)} />

                        </div>

                        <div class="mb-3">
                            <label className="form-label">Name</label>
                            <input type="text" class="form-control"
                                value={uname}
                                onChange={this.unameHandling.bind(this)} />

                        </div>

                        <div class="mb-3">
                            <label className="form-label">Email</label>
                            <input type="email" class="form-control"
                                value={email}
                                onChange={this.emailHandling.bind(this)} />
                        </div>

                        <div class="mb-3">
                            <label className="form-label">Phone</label>
                            <input type="number" class="form-control"
                                value={phone}
                                onChange={this.phoneHandling.bind(this)} />
                        </div>


                        <div class="mb-3">
                            <button type="submit" class="btn btn-primary">CREATE ACCOUNT</button>
                        </div>

                        <p class="text-primary">{txtConformation}</p>

                    </form>


                </div>

            </div>
        </>)
    }
}
