import React, { Component } from 'react'

import { connect } from 'react-redux'

import EmpAction from './../../actions/empaction.js'

import { bindActionCreators } from 'redux'

 class Rrc extends Component 
 {
   
  constructor(props) 
  {
    super(props);
  
    this.state = {}
  }
  
  componentDidMount()
  {
      this.props.EmpAction();
  }




  render() 
  {
    return (<>
               <div className="container text-center p-3">
                    
                    <h2>React_Redux Connection</h2>

                </div>
                    <div className="container  p-3 w-25">
                  {
                     this.props.empdata.map((res,i)=>
                     {
                             
                        return(<ul key={i}>
                                        <li>{res.id}</li>
                                        <li>{res.name}</li>
                                        <li>{res.mail}</li>
                                        <li>{res.phone}</li>
                              </ul>)
                        
                      
                    })




                  }

                </div>
           </>)
  }
}

const mapStateToProps = (state)=>
{
    console.log(state)

   return({
       empdata:state.empReducer
   })
}

const mapDispatchToProps = (dispatch)=>
{
      return bindActionCreators({EmpAction},dispatch)
}


export default connect(mapStateToProps,mapDispatchToProps)(Rrc);
