import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'

var idno;

 class Edit extends Component 
 
 {

    constructor(props) 
    {
      super(props)
    
      this.state = {}
    }
                        
    
    

    render() {
            
         idno=this.props.match.params.userid;
         
             console.log(idno)

             var {userid , id , name , mail , phone} = this.state

        return (<>
                    <div className="container text-center p-3">

                        <h3>User_Data_Edit</h3>

                        <div className="container w-25">

                        <div class="mb-3">
                           <label   className="form-label">Id</label>
                           <input type="text" class="form-control"/>
                        </div>

                              <Link to="/localapi1" className="btn btn-success">Go_To_Back</Link>

                        </div>
                        
                    </div>
        </>)
    }

    componentDidMount()
    {
           const url=`http://localhost:3001/user/${idno}` 

           axios.get(url).then((result) => this.setState(result.data));
    }
}


export default Edit;
