 import { Link } from 'react-router-dom'

 import axios from 'axios';
 

 var idno;
 export default class View extends Component {
 
  constructor(props)
  {
      super(props);
      this.state=
      {

      }
  }

 
   render()
    {
       idno = (this.props.match.params.userid)
     return (<>
        
             
      
            <div className="container m-2 p-5 text-center">
                 <h1>User_View_Data</h1>
                 <p> {idno} th Record_Data </p>

                 <ul>
                      <li>{this.state.userId}</li>
                      <li>{this.state.id}</li>
                      <li>{this.state.title}</li>
                      <li>{this.state.completed}</li>

                 </ul>

            </div>
       
       </>)
   }

   
   componentDidMount()
   {      
           const url=`http://localhost:3001/user/${idno}`

           axios.get(url).then( (result) => this.setState(result.data));
   }
 }
