import React, { Component } from 'react'

import { Link } from 'react-router-dom'

import axios from 'axios';




 var idno;

 class View extends Component 

{

    

   constructor(props)
   {
       super(props)
       this.state=
       {
           // viewData:{}   
           
       }
   }

 


    render() 
    {
        
          idno = this.props.match.params.userid

          console.log(idno)

        return (<>

            <div className="container m-2 p-5 text-center">
                <h1>User _id Data</h1>

                    <p>{idno}th record</p>
                 
                
                       <ul> 

                              <li>{this.state.userid}</li>
                              <li>{this.state.id}</li>
                              <li>{this.state.title}</li>
                              <li>{this.state.completed}</li>

                       </ul>
                       
                        <Link to="/localapi1" className="btn btn-success">Go_Back</Link>

            </div>
        </>)
    }

    componentDidMount()
    {
       
        const url = `http://localhost:3001/user/${idno}`

        axios.get(url).then((result) => this.setState(result.data));
    }
 

}
