import React, { Component } from 'react'

import axios from 'axios';




var userid;
export default class View extends Component {

   constructor(props)
   {
       super(props);
       this.state=
       {
             viewData:{}

       }
   }

 


    render() {
          userid = this.props.match.params.userid
        console.log(userid)
        return (
            <div className="container m-2 p-5 text-center">
                <h1>User _id Data</h1>

                <p> {userid} th Record Data </p> 
                
                       <ul>

                              <li>{this.setState.userid}</li>
                              <li>{this.setState.id}</li>
                              <li>{this.setState.title}</li>
                              <li>{this.setState.compl}</li>

                       </ul>

            </div>
        )
    }

    componentDidMount()
    {
        const url=`http://localhost:3001/user`

            axios.get(`${url}/${userid}`).then( (result) => this.setState(result.data));
    }
}
