 import React, { Component } from 'react'

 import axios from 'axios';

 //Golbal Variable From Online
 const url=`https://jsonplaceholder.typicode.com/todos`
 
 class RestApi1 extends Component
{
       
       constructor(props) {
         super(props)
       
         this.state = 
         {
                 userdata:[]  //empty array for accessing the url data
         }
       }
       
       
       
       
       
       
        render()
         {
            return (<>

            <div className="container-fluid py-5 bg-dark">

            <h2 className="text-center text-white ">React_RestApi_Data Loading through Global URLs</h2>

            <div className="container p-y bg-light">
               
               {
                       this.state.userdata.map( (res,i)=> 
                       {
                            return(<ul key={i}>
                                    
                                    <li>{res.userId}</li>
                                    <li>{res.id}</li>
                                    <li>{res.title}</li>
                                    <li>{res.completed}</li>
                               
                            
                            </ul>)
                       })
               }
            

            </div>
            </div>
                    
                </>)
         }

         componentDidMount()
         {
                 axios.get(url).then( (result)=> this.state({userdata:result.data}));
         }
 }
 

 export default RestApi1;
