import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios';


//Global 
var idno;

export default class View extends Component {

    constructor(props) {
        super(props)
        this.state = {}//empty
    }


    render() {
        idno = this.props.match.params.userid
        //this.props-> history, location, match 
        console.log()

        return (<>
            <div className="container p-5 text-center">
                <h2>VIEW COMPONENT PAGE</h2>
                <p>
                    {idno} th Record Data
                </p>

                <ul>
                    <li>{this.state.userid}</li>
                    <li>{this.state.id}</li>
                    <li>{this.state.title}</li>
                  
                </ul>

                <Link className="btn btn-success" to="/localapi1">GOTO BACK</Link>

            </div>
        </>)
    }


    //RESTAPI_CALLS
     

    componentDidMount()
    {
        const url = `http://localhost:3001/user/${idno}`  
      
        axios.get(url).then((result) => this.setState(result.data));
        //Update apidata to empty state object via setState
    }

     


}
